function [W] = reg_log_SGD(X, Y, l = 10^(-5), step=0.1)  n = size(X, 1);  X = [ones(n, 1), X];  m = size(X, 2);  W = zeros(m, 1);    rp = randperm(n);   Yst = Y(rp);   Xst = X(rp, :);   m = size(Xst, 2);    H = (Xst'*Xst);    det(H);    B = diag(1./(1+e.^(Yst.*(Xst*W))));  if(det(H) == 0)    grad = l*W - Xst'*B*Yst;  else    grad = (-1)*Xst'*B*Yst;  end    W_pref = H^(-1)*Xst'*Yst;        Y_pre = Xst*W_pref;      c=0.01;    for e=1:1      for i = 1:n        if(det(H) == 0)          W = W + 2*(c/sqrt(i+(e-1)*n))*(l*W - Xst'*B*Yst)';         else          W = W + 2*(c/sqrt(i+(e-1)*n))*((-1)*Xst'*B*Yst)';         end      end      Y_pre2 = Xst*W;     end    end