function [W, W_epoch] = reg_log_SGD(X, Y, step=0.1)  n = size(X, 1);  X = [ones(n, 1), X];    rp = randperm(n);   Yst = Y(rp);   Xst = X(rp, :);   m = size(Xst, 2);  W = zeros(m, 1);  W_epoch = [];    c=0.01;  for e=1:50    for i = 1:n      W = W + (c/sqrt(i+(e-1)*n))*(Yst(i)/(1 + exp(Yst(i)*Xst(i,:)*W)))*Xst(i,:)';    end    W_epoch = [W_epoch, W];  end    end